    { text: "Cubana", value: 10 },
    { text: "Dominicana", value: 1 },
    { text: "Ecuatoriana", value: 11 },
    { text: "Española", value: 12 },
    { text: "Estadounidense", value: 13 },
    { text: "Francesa", value: 14 },
    { text: "Griega", value: 15 },
    { text: "Guatemalteca", value: 16 },
    { text: "Hondureña", value: 17 },
    { text: "Inglesa", value: 18 },
    { text: "Italiana", value: 19 },
    { text: "Japonesa", value: 20 },
    { text: "Mexicana", value: 21 },
    { text: "Nicaragüense", value: 22 },
    { text: "Panameña", value: 23 },
    { text: "Paraguaya", value: 24 },
    { text: "Peruana", value: 25 },
    { text: "Portuguesa", value: 26 },
    { text: "Salvadoreña", value: 27 },
    { text: "Sueca", value: 28 },
    { text: "Suiza", value: 29 },
    { text: "Uruguaya", value: 30 },
    { text: "Venezolana", value: 31 },
  ];
  provincias = [
    { text: "Azua", value: 1 },
    { text: "Bahoruco", value: 2 },
    { text: "Barahona", value: 3 },
    { text: "Dajabón", value: 4 },
    { text: "Duarte", value: 5 },
    { text: "Elías Piña", value: 6 },
    { text: "El Seibo", value: 7 },
    { text: "Espaillat", value: 8 },
    { text: "Hato Mayor", value: 9 },
    { text: "Hermanas Mirabal", value: 10 },
    { text: "Independencia", value: 11 },
    { text: "La Altagracia", value: 12 },
    { text: "La Romana", value: 13 },
    { text: "La Vega", value: 14 },
    { text: "María Trinidad Sánchez", value: 15 },
    { text: "Monseñor Nouel", value: 16 },
    { text: "Monte Cristi", value: 17 },
    { text: "Monte Plata", value: 18 },
    { text: "Pedernales", value: 19 },
    { text: "Peravia", value: 20 },
    { text: "Puerto Plata", value: 21 },
    { text: "Samaná", value: 22 },
    { text: "Sánchez Ramírez", value: 23 },
    { text: "San Cristóbal", value: 24 },
    { text: "San José de Ocoa", value: 25 },
    { text: "San Juan", value: 26 },
    { text: "San Pedro de Macorís", value: 27 },
    { text: "Santiago", value: 28 },
    { text: "Santiago Rodríguez", value: 29 },
    { text: "Santo Domingo", value: 30 },
    { text: "Valverde", value: 31 },
  ];
  vehiculos = [
    { text: "Hyundai Santa FE SEL 2019 - Blanco", value: "1" },
    { text: "Hyundai Santa FE SEL 2019 - Blanco", value: "1" },
    { text: "Hyundai Santa FE Limited 2021- Negro", value: "2" },
    { text: "Hyundai Tucson SEL 2020 - Gris", value: "3" },
    { text: "Honda CR-V EXL 2021 - Negro", value: "4" },
    { text: "Honda CR-V Touring 2022  - Blanco", value: "5" },
    { text: "Honda CR-V EX 2020 - Azul", value: "6" },
    { text: "Honda Accord Sport 2.0T 2019 - Blanco", value: "7" },
    { text: "Honda Accord Sport EX 2019 - Gris", value: "8" },
    { text: "Honda Civic EX-T 2019 - Blanco", value: "9" },
    { text: "Honda Civic Touring 2019 - Blanco", value: "10" },
    { text: "Toyota RAV4 XLE 2020 - Negro", value: "11" },
    { text: "Toyota RAV4 Adventure 2022 - Gris", value: "12" },
    { text: "Toyota Camry SE 2019 - Azul", value: "13" },
    { text: "Toyota Land Cruiser VX.L 2020 - Negro", value: "14" },
    { text: "Toyota Corolla LE 2019 - Blanco", value: "15" },
    { text: "Toyota Corolla SE 2020 - Gris", value: "16" },
    { text: "Toyota Corolla CROSS 2019 - Blanco", value: "17" },
    { text: "Toyota Corolla CROSS 2019 - Rojo", value: "16" },
    { text: "Toyota LC Prado TXL 2020  - Gris", value: "17" },
    { text: "Toyota 4Runner 2018 - Blanco", value: "18" },
    { text: "Kia Sorento Limited 2021  - Negro", value: "19" },
    { text: "Kia Sportage Limited 2023 - Gris", value: "20" },
    { text: "Chevrolet TAHOE XRT 2020 - Negro", value: "21" },
    { text: "Chevrolet TAHOE High Country 2020 - Negro", value: "22" },
    { text: "Chevrolet Suburban High Country 2020 - Negro", value: "23" },
    { text: "Cadillac Scalade 600 2020 - Negro", value: "24" },
    { text: "Jeep Grand Cherokee Summit 2019 - Gris", value: "25" },
    { text: "Kia Seltos Limited 2020 - Blanco", value: "26" },
    { text: "Lexus RX350 F-Sport 2020 - Blanco", value: "27" },
    { text: "Lexus UX 2021 - Blanco", value: "28" },
    { text: "Lexus LX 600 2023 - Negro", value: "29" },
    { text: "Nissan Armada 2021 - Gris", value: "30" },
  ];
  form!: FormGroup;
  constructor(private fb: FormBuilder) {
    this.crearFormulario();
  }

  get nombreNoValido() {
    return this.form.get('nombres')?.invalid && this.form.get('nombres')?.touched;
  }
  get apellidoNoValido() {
    return this.form.get('apellidos')?.invalid && this.form.get('apellidos')?.touched;
  }
  get fechaNacimientoNoValido() {
    return this.form.get('fechaNacimiento')?.invalid && this.form.get('fechaNacimiento')?.touched;
  }
  get direccionNoValido() {
    return this.form.get('direccion')?.invalid && this.form.get('direccion')?.touched;
  }
  get NacionalidadNoValido() {
    return this.form.get('Nacionalidad')?.invalid && this.form.get('Nacionalidad')?.touched;
  }
  get sectorNoValido() {
    return this.form.get('sector')?.invalid && this.form.get('sector')?.touched;
  }
  get provinciaNoValido() {
    return this.form.get('provincia')?.invalid && this.form.get('provincia')?.touched;
  }
  get numtelNoValido() {
    return this.form.get('numtel')?.invalid && this.form.get('numtel')?.touched;
  }
  get correoelectNoValido() {
    return this.form.get('correoelect')?.invalid && this.form.get('correoelect')?.touched;
  }
  get fechaEntregaNoValido() {
    return this.form.get('fechaEntrega')?.invalid && this.form.get('fechaEntrega')?.touched;
  }
  get fechaDevolucionNoValido() {
    return this.form.get('fechaDevolucion')?.invalid && this.form.get('fechaDevolucion')?.touched;
  }
  get vehiculoNoValido() {
    return this.form.get('vehiculo')?.invalid && this.form.get('vehiculo')?.touched;
  }
  get cantConductoresNoValido() {
    return this.form.get('cantConductores')?.invalid && this.form.get('cantConductores')?.touched;
  }
  get cedulaPasaporteNoValido() {
    return this.form.get('cedulaPasaporte')?.invalid && this.form.get('cedulaPasaporte')?.touched;
  }
  get metodoPagoNoValido() {
    return this.form.get('metodoPago')?.invalid && this.form.get('metodoPago')?.touched;
  }
  get licenciaNoValido() {
    return this.form.get('licencia')?.invalid && this.form.get('licencia')?.touched;
  }

  crearFormulario() {
    this.form = this.fb.group(
      {
        nombres: ['', [Validators.required, Validators.maxLength(50)]],
        apellidos: ['', [Validators.required, Validators.maxLength(50)]],
        fechaNacimiento: ['', Validators.required],
        Nacionalidad: ['', Validators.required],
        direccion: ['', Validators.required, Validators.maxLength(500)],
        sector: ['', Validators.required],
        provincia: ['', Validators.required],
        numtel: ['', Validators.required],
        correoelect: ['', [Validators.required, Validators.pattern('[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$')]],
        fechaEntrega: ['', Validators.required],
        fechaDevolucion: ['', Validators.required],
        vehiculo: ['', Validators.required],
        cantConductores: ['', Validators.required],
        millasIlimitadas: [''],
        gps: [''],
        asisCarret: [''],
        pasoRapido: [''],
        tipoPersona: ['', Validators.required],
        metodoPago: ['', Validators.required],
        cedulaPasaporte: ['', Validators.required],
        licencia: ['', Validators.required],
        comentarios: [''],
      }
    )
  }

  limpiar() {
    this.form.reset();
  }

  @ViewChild('tableReservaciones', { static: false }) tableReservaciones!: ElementRef;



  ngOnInit(): void {
    $(document).ready(() => {
      $(".seelects").select2({
        placeholder: "Seleccione...",
        allowClear: true,
        language: "es",
      });

      this.nacionalidades.forEach(item => {
        $("#nacionalidadSelect").append('<option value="' + item.value + '">' + item.text + "</option>");
      });
      this.provincias.forEach(item => {
        $("#provinciasSelect").append('<option value="' + item.value + '">' + item.text + "</option>");
      });

      this.vehiculos.forEach(item => {
        $("#vehiculosSelect").append('<option value="' + item.value + '">' + item.text + "</option>");
      });
    });
  }

  ngAfterViewInit(): void {
    this.tableReservaciones.nativeElement
  }

  onSubmit() {
    if (this.form.invalid) {
      return Object.values(this.form.controls).forEach(control => {
        control.markAllAsTouched();
      })
    } else {

      const nacionalidadTexto = this.nacionalidades.find(
        (item) => item.value === parseInt(this.form.value.Nacionalidad)
      )?.text;

      const provinciaTexto = this.provincias.find(
        (item) => item.value === parseInt(this.form.value.provincia)
      )?.text;

      // Encuentra el texto correspondiente al vehículo
      const vehiculoTexto = this.vehiculos.find(
        (item) => item.value === this.form.value.vehiculo
      )?.text;

      this.reservaciones.push({
        nombres: this.form.value.nombres,
        apellidos: this.form.value.apellidos,
        fechaNacimiento: this.form.value.fechaNacimiento,
        nacionalidad: nacionalidadTexto,
        direccion: this.form.value.direccion,
        sector: this.form.value.sector,
        provincia: provinciaTexto,
        tel: this.form.value.numtel,
        email: this.form.value.correoelect,
        fechaEntrega: this.form.value.fechaEntrega,
        fechaDevolucion: this.form.value.fechaDevolucion,
        vehiculo: vehiculoTexto,
        cantConductores: this.form.value.cantConductores,
        opciones:
          (this.form.value.millasIlimitadas === true ? "Millas ilimitadas" : "") +
          (this.form.value.gps === true ? ", GPS" : "") +
          (this.form.value.asisCarret === true ? ", Asist. Carrt 24/7" : "") +
          (this.form.value.pasoRapido === true ? ", Paso rápido" : ""),
        tipoPersona: this.form.value.tipoPersona,
        metodoPago: this.form.value.metodoPago,
        cedulaPasaporte: this.form.value.cedulaPasaporte,
        licencia: this.form.value.licencia,
        comentarios: this.form.value.comentarios,
      });

      console.log(this.reservaciones);

      const table = this.tableReservaciones.nativeElement as HTMLTableElement;
      const tbody = table.getElementsByTagName('tbody')[0];
      tbody.innerHTML = '';

      for (let i = 0; i < this.reservaciones.length; i++) {
        const reserva = this.reservaciones[i];

        const row = tbody.insertRow(i);

        const cell1 = row.insertCell(0);
        const cell2 = row.insertCell(1);
        const cell3 = row.insertCell(2);
        const cell4 = row.insertCell(3);
        const cell5 = row.insertCell(4);
        const cell6 = row.insertCell(5);
        const cell7 = row.insertCell(6);
        const cell8 = row.insertCell(7);
        const cell9 = row.insertCell(8);
        const cell10 = row.insertCell(9);
        const cell11 = row.insertCell(10);
        const cell12 = row.insertCell(11);
        const cell13 = row.insertCell(12);
        const cell14 = row.insertCell(13);
        const cell15 = row.insertCell(14);
        const cell16 = row.insertCell(15);
        const cell17 = row.insertCell(16);
        const cell18 = row.insertCell(17);
        const cell19 = row.insertCell(18);

        cell1.innerHTML = reserva.nombres;
        cell2.innerHTML = reserva.apellidos;
        cell3.innerHTML = reserva.fechaNacimiento;
        cell4.innerHTML = reserva.nacionalidad;
        cell5.innerHTML = reserva.direccion;
        cell6.innerHTML = reserva.sector;
        cell7.innerHTML = reserva.provincia;
        cell8.innerHTML = reserva.tel;
        cell9.innerHTML = reserva.email;
        cell10.innerHTML = reserva.fechaEntrega;
        cell11.innerHTML = reserva.fechaDevolucion;
        cell12.innerHTML = reserva.vehiculo;
        cell13.innerHTML = reserva.cantConductores;
        cell14.innerHTML = reserva.opciones;
        cell15.innerHTML = reserva.tipoPersona;
        cell16.innerHTML = reserva.metodoPago;
        cell17.innerHTML =
          '<a href="' + reserva.cedulaPasaporte + '" target="_blank">Ver</a>';
        cell18.innerHTML =
          '<a href="' + reserva.licencia + '" target="_blank">Ver</a>';
        cell19.innerHTML = reserva.comentarios;
      }

      this.limpiar();
    }
  }
}
<div class="car_rental-reservaciones">
  <div class="car_rental-reservaciones-header">
    <h1>Reservaciones</h1>
  </div>
  <div class="car_rental-form-container">
    <form id="car_rental-form" [formGroup]="form" (ngSubmit)="onSubmit()">
      <div class="caption">
        <span>Información personal</span>
        <hr />
      </div>
      <div class="row">

        <div class="form-group col-md-6 mb-3">
          <label for="nombres" class="form-label">Nombres</label>
          <input type="text" name="nombres" class="form-control" id="nombres" maxlength="50" formControlName="nombres" [class.is-invalid]="nombreNoValido" required placeholder="Fulano" />
          <small *ngIf="nombreNoValido" class="text-danger">Ingrese un nombre.</small>
        </div>

        <div class="form-group col-md-6 mb-3">
          <label for="apellidos" class="form-label">Apellidos</label>
          <input type="text" name="apellidos" class="form-control" id="apellidos" maxlength="50" formControlName="apellidos" [class.is-invalid]="apellidoNoValido" required placeholder="De Tal" />
          <small *ngIf="apellidoNoValido" class="text-danger">Ingrese un apellido.</small>
        </div>

      </div>

      <div class="row">
        <div class="form-group col-md-6 mb-3">
          <label for="fechaNacimiento" class="form-label">Fecha de nacimiento</label>
          <input type="date" name="fechaNacimiento" class="form-control" id="fechaNacimiento" formControlName="fechaNacimiento" [class.is-invalid]="fechaNacimientoNoValido" required />
          <small *ngIf="fechaNacimientoNoValido" class="text-danger">Ingrese una fecha de nacimiento.</small>
        </div>

        <div class="form-group col-md-6 mb-3">
          <label for="Nacionalidad" class="form-label">Nacionalidad</label>
          <select name="Nacionalidad"
                  class="form-select"
                  id="nacionalidadSelect"
                  formControlName="Nacionalidad"
                  [class.is-invalid]="NacionalidadNoValido"
                  required>
            <option selected>Seleccione</option>
          </select>
          <small *ngIf="NacionalidadNoValido" class="text-danger">Seleccione una nacionalidad.</small>
        </div>
      </div>

      <div class="row">
        <div class="form-group col-md-12 mb-3">
          <label for="direccion" class="form-label">Dirección</label>
          <input type="text" name="direccion" class="form-control" id="direccion" formControlName="direccion" [class.is-invalid]="direccionNoValido" required placeholder="C. Bienvenido García Gautier, Santo Domingo" />
          <small *ngIf="direccionNoValido" class="text-danger">Ingrese una direccion.</small>
        </div>
      </div>

      <div class="row">
        <div class="form-group col-md-6 mb-3">
          <label for="sector" class="form-label">Sector</label>
          <input type="text" name="sector" class="form-control" formControlName="sector" id="sector" [class.is-invalid]="sectorNoValido" required placeholder="Los Restauradores" />
          <small *ngIf="sectorNoValido" class="text-danger">Ingrese un sector.</small>
        </div>
        <div class="form-group col-md-6 mb-3">
          <label for="provincia" class="form-label">Provincia</label>
          <select name="provincia"
                  class="form-select"
                  id="provinciasSelect"
                  formControlName="provincia"
                  [class.is-invalid]="provinciaNoValido"
                  required>
            <option selected>Seleccione</option>
          </select>
          <small *ngIf="provinciaNoValido" class="text-danger">Seleccione una provincia.</small>
        </div>
      </div>

      <div class="row">
        <div class="form-group col-md-6 mb-3">
          <label for="numtel" class="form-label">Número de télefono</label>
          <input type="tel" name="numtel" id="numtel" class="form-control" formControlName="numtel" [class.is-invalid]="numtelNoValido" required placeholder="809-699-6525" />
          <small *ngIf="numtelNoValido" class="text-danger">Ingrese un número de teléfono.</small>
        </div>

        <div class="form-group col-md-6 mb-3">
          <label for="correoelect" class="form-label">Correo electrónico</label>
          <input type="email" id="correoelect" class="form-control" name="correoelect" formControlName="correoelect" [class.is-invalid]="correoelectNoValido" required placeholder="fulanodetal@gmail.com" />
          <small *ngIf="correoelectNoValido" class="text-danger">Ingrese un correo electrónico.</small>
        </div>
      </div>

      <div class="caption">
        <span>Información de renta</span>
        <hr />
      </div>

      <div class="row">
        <div class="form-group col-md-6 mb-3">
          <label for="fechaEntrega" class="form-label">Fecha de entrega</label>
          <input type="date" id="fechaEntrega" class="form-control" name="fechaEntrega" formControlName="fechaEntrega" [class.is-invalid]="fechaEntregaNoValido" required />
          <small *ngIf="fechaEntregaNoValido" class="text-danger">Ingrese una fecha de entrega.</small>
        </div>

        <div class="form-group col-md-6 mb-3">
          <label for="fechaDevolucion" class="form-label">Fecha de devolución</label>
          <input type="date" id="fechaDevolucion" class="form-control" name="fechaDevolucion" formControlName="fechaDevolucion" [class.is-invalid]="fechaDevolucionNoValido" required />
          <small *ngIf="fechaDevolucionNoValido" class="text-danger">Ingrese una fecha de devolución.</small>
        </div>
      </div>

      <div class="row">
        <div class="form-group col-md-6 mb-3">
          <label for="vehiculo" class="form-label">Vehiculo a elegir (disponibles)</label>
          <select name="vehiculo"
                  class="form-select"
                  id="vehiculosSelect"
                  formControlName="vehiculo"
                  [class.is-invalid]="vehiculoNoValido"
                  required>
            <option selected>Seleccione</option>
          </select>
          <small *ngIf="vehiculoNoValido" class="text-danger">Seleccione un vehículo.</small>
        </div>
        <div class="form-group col-md-6 mb-3">
          <label for="cantConductores" class="form-label">Cantidad de conductores</label>
          <input type="number" name="cantConductores" class="form-control" id="cantConductores" formControlName="cantConductores" [class.is-invalid]="cantConductoresNoValido" required />
          <small *ngIf="cantConductoresNoValido" class="text-danger">Ingrese personas de conductores que tomarán el vehículo.</small>
        </div>
      </div>

      <div class="caption">
        <span>Opcionales</span>
        <hr />
      </div>

      <div class="form-car-rental-opciones_groups">
        <div class="form_radio_select-group">
          <label for="millasIlimitadas" class="form-check-label">Millas ilimitadas</label>
          <input type="checkbox" class="form-check-input" name="millasIlimitadas" id="millasIlimitadas" formControlName="millasIlimitadas" />
        </div>
        <div class="form_radio_select-group">
          <label for="gps" class="form-check-label">GPS</label>
          <input type="checkbox" class="form-check-input" name="gps" id="gps" formControlName="gps" />
        </div>
        <div class="form_radio_select-group">
          <label for="asisCarret" class="form-check-label">Asistencia de carretera 24/7</label>
          <input type="checkbox" class="form-check-input" name="asisCarret" id="asisCarret" formControlName="asisCarret" />
        </div>
        <div class="form_radio_select-group">
          <label for="pasoRapido" class="form-check-label">Paso rápido de peaje</label>
          <input type="checkbox" class="form-check-input" name="pasoRapido" id="pasoRapido" formControlName="pasoRapido" />
        </div>
      </div>

      <div class="caption">
        <span>Pago</span>
        <hr />
      </div>

      <div class="form-car-rental-opciones-radio_groups">
        <span>Tipo de persona</span>
        <hr />

        <div class="row">
          <div class="form_radio_select-group col-md-3 mb-3">
            <label for="personaFisica" class="form-check-label"> Fisica </label>
            <input type="radio" class="form-check-input" name="tipoPersona" id="personaFisica" value="Fisica" formControlName="tipoPersona" />
          </div>

          <div class="form_radio_select-group col-md-3 mb-3">
            <label for="personaJuridica" class="form-check-label"> Jurídica </label>
            <input type="radio" class="form-check-input" name="tipoPersona" id="personaJuridica" value="Jurídica" formControlName="tipoPersona" />
          </div>
        </div>
        <small *ngIf="" class="text-danger">Seleccione un tipo de persona.</small>

      </div>

      <div class="form-car-rental-opciones-radio_groups">
        <span>Método de pago</span>
        <hr />
        <div class="row">
          <div class="form_radio_select-group col-md-6 mb-3">
            <label for="tarjeta" class="form-check-label">Tarjeta de crédito débito</label>
            <input type="radio" class="form-check-input" name="metodoPago" id="metodoPagoTj" value="Tarjeta" formControlName="metodoPago" />
          </div>
          <div class="form_radio_select-group col-md-3 mb-3">
            <label for="cheque" class="form-check-label">Cheque</label>
            <input type="radio" class="form-check-input" name="metodoPago" id="metodoPagoCheque" value="Cheque" formControlName="metodoPago" />
          </div>
          <div class="form_radio_select-group col-md-3 mb-3">
            <label for="Efectivo" class="form-check-label">Efectivo</label>
            <input type="radio" class="form-check-input" name="metodoPago" id="metodoPagoEfectivo" value="Efectivo" formControlName="metodoPago" />
          </div>
          <small *ngIf="metodoPagoNoValido" class="text-danger">Seleccione un método de pago.</small>
        </div>
      </div>

      <div class="caption">
        <span>Documetos</span>
        <hr />
      </div>
      <div class="form-group">
        <label for="cedulaPasaporte" class="form-label">Foto de cédula o pasaporte</label>
        <input type="file" class="form-control" name="cedulaPasaporte" id="cedulaPasaporte" required formControlName="cedulaPasaporte" [class.is-invalid]="cedulaPasaporteNoValido" />
        <small *ngIf="cedulaPasaporteNoValido" class="text-danger">Inserte un escáner de la cédula o pasaporte.</small>
      </div>
      <div class="form-group">
        <label for="licencia" class="form-label">Foto de licencia de conducir</label>
        <input type="file" class="form-control" name="licencia" id="licencia" required formControlName="licencia" [class.is-invalid]="licenciaNoValido" />
        <small *ngIf="licenciaNoValido" class="text-danger">Inserte un escáner de la licencia de conducir.</small>
      </div>

      <div class="caption">
        <span>Comentarios para trato preferencial o añadidos</span>
        <hr />
      </div>
      <div class="form-group">
        <label for="comentarios" class="form-label">Comentarios</label>
        <textarea name="comentarios" class="form-control" id="comentarios" cols="30" rows="10" formControlName="comentarios"></textarea>
      </div>
      <div class="form-group">
        <input type="submit" value="Enviar reservación" id="submit-reservation" class="w-100" />
      </div>
    </form>
  </div>


</div>

<div class="table-reservaciones-container">
  <hr />
  <div class="table-reservaciones-header">
    <h2>Reservaciones</h2>
  </div>
  <div class="table-reservaciones-contain">
    <table #tableReservaciones id="table-reservaciones" class="table table-striped table-hover table-responsive">
      <thead>
        <tr>
          <th>Nombres</th>
          <th>Apellidos</th>
          <th>Fecha de nacimiento</th>
          <th>Nacionalidad</th>
          <th>Dirección</th>
          <th>Sector</th>
          <th>Provincia</th>
          <th>Celular</th>
          <th>email</th>
          <th>Fecha de entrega</th>
          <th>Fecha devolución</th>
          <th>Vehiculo</th>
          <th>Cant. Conductores</th>
          <th>Opcionales</th>
          <th>Tipo persona</th>
          <th>Método pago</th>
          <th>Cédula</th>
          <th>Licencia</th>
          <th>Comentarios</th>
        </tr>
      </thead>
      <tbody></tbody>
    </table>
  </div>

</div>
<hr />
<h3 class="nt-5">Formulario</h3>
<pre>Estado formulario: {{form.valid}}
<br />
Status: {{form.status}}</pre>

<pre>
{{form.value | json}}
</pre>
